// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.8.4;

// helper methods for interacting with ERC20 tokens and sending ETH that do not consistently return true/false
library TransferHelper {
    function safeApprove(address _token, address _to, uint256 _value) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = _token.call(
            abi.encodeWithSelector(0x095ea7b3, _to, _value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper::safeApprove: approve failed"
        );
    }

    function safeTransfer(
        address _token,
        address _to,
        uint256 _value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = _token.call(
            abi.encodeWithSelector(0xa9059cbb, _to, _value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper::safeTransfer: transfer failed"
        );
    }

    function safeTransferFrom(
        address _token,
        address _from,
        address _to,
        uint256 _value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = _token.call(
            abi.encodeWithSelector(0x23b872dd, _from, _to, _value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper::transferFrom: transferFrom failed"
        );
    }

    function safeTransferETH(address _to, uint256 _value) internal {
        (bool success, ) = _to.call{value: _value}(new bytes(0));
        require(
            success,
            "TransferHelper::safeTransferETH: ETH transfer failed"
        );
    }
}

contracts/Monitor.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.8.4;

import "@chainlink/contracts/src/v0.8/AutomationCompatible.sol";
import "./libraries/TransferHelper.sol";
import "./interfaces/ICloseCallback.sol";
import "./interfaces/IFactory.sol";
import "./interfaces/IDEXAggregator.sol";
import "./interfaces/IMetaAggregator.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IPool.sol";

contract Monitor is AutomationCompatibleInterface, ICloseCallback {
    address public manager;
    address public keeper;
    address public factory;
    address public positionStorage;
    address public metaAggregator;
    uint256 public batchSize;
    uint256 public monitorSize;
    uint256 public startIndex;

    error Forbidden(address sender);
    error TooHighValue(uint256 value, uint256 max);
    error TooLowValue(uint256 value, uint256 min);
    error InsufficientOutput();

    event SetManager(address manager);
    event SetKeeper(address keeper);
    event SetFactory(address factory);
    event SetMetaAggregator(address metaAggregator);
    event SetBatchSize(uint256 batchSize);
    event SetMonitorSize(uint256 monitorSize);
    event SetStartIndex(uint256 startIndex);
    event ProcessBatch(
        uint256 count,
        bytes performData,
        uint256 usedGas,
        uint256 gasPrice
    );

    constructor(
        address _keeper,
        address _factory,
        uint256 _batchSize,
        uint256 _monitorSize,
        uint256 _startIndex
    ) {
        manager = msg.sender;
        keeper = _keeper;
        factory = _factory;
        positionStorage = IFactory(_factory).positionStorage();
        batchSize = _batchSize;
        monitorSize = _monitorSize;
        startIndex = _startIndex;
    }

    modifier onlyManager() {
        if (msg.sender != manager) revert Forbidden(msg.sender);
        _;
    }

    function setManager(address _manager) external onlyManager {
        manager = _manager;

        emit SetManager(_manager);
    }

    function setKeeper(address _keeper) external onlyManager {
        keeper = _keeper;

        emit SetKeeper(_keeper);
    }

    function setFactory(address _factory) external onlyManager {
        factory = _factory;
        positionStorage = IFactory(_factory).positionStorage();

        emit SetFactory(_factory);
    }

    function setMetaAggregator(
        address _newMetaAggregator
    ) external onlyManager {
        metaAggregator = _newMetaAggregator;

        emit SetMetaAggregator(_newMetaAggregator);
    }

    function setBatchSize(uint256 _batchSize) external onlyManager {
        if (_batchSize > monitorSize)
            revert TooHighValue(_batchSize, monitorSize);
        batchSize = _batchSize;

        emit SetBatchSize(_batchSize);
    }

    function setMonitorSize(uint256 _monitorSize) external onlyManager {
        if (_monitorSize < batchSize)
            revert TooLowValue(_monitorSize, batchSize);
        monitorSize = _monitorSize;

        emit SetMonitorSize(_monitorSize);
    }

    function setStartIndex(uint256 _startIndex) external onlyManager {
        startIndex = _startIndex;

        emit SetStartIndex(_startIndex);
    }

    function closeCallback(
        address _tokenIn,
        address _tokenOut,
        uint256 _minAmountOut,
        bytes calldata _data
    ) external override {
        uint256 balance = IERC20(_tokenIn).balanceOf(address(this));
        uint256 amountOut;
        if (metaAggregator == address(0) || _data.length == 0) {
            IFactory _factory = IFactory(factory);
            IDEXAggregator aggregator = IDEXAggregator(
                _factory.dexAggregator()
            );

            TransferHelper.safeTransfer(_tokenIn, address(aggregator), balance);
            (amountOut, ) = aggregator.swap(
                address(0),
                _tokenIn,
                _tokenOut,
                _minAmountOut,
                address(this)
            );
        } else {
            IMetaAggregator aggregator = IMetaAggregator(metaAggregator);

            TransferHelper.safeTransfer(_tokenIn, address(aggregator), balance);
            amountOut = aggregator.swap(
                _tokenIn,
                _tokenOut,
                _minAmountOut,
                address(this),
                _data
            );
        }

        if (amountOut < _minAmountOut) revert InsufficientOutput();
        TransferHelper.safeTransfer(_tokenOut, address(msg.sender), amountOut);
    }

    function checkUpkeep(
        bytes calldata /* checkData */
    )
        external
        view
        override
        returns (bool upkeepNeeded, bytes memory performData)
    {
        IPositionStorage _positionStorage = IPositionStorage(positionStorage);
        bytes32[] memory batchPositionKeys = new bytes32[](batchSize);
        uint256 count;
        uint256 i;

        uint256 positionLength = _positionStorage.positionLength();
        if (startIndex < positionLength) {
            for (uint256 j = startIndex; j < positionLength; j++) {
                bytes32 positionKey = _positionStorage.positionKeys(j);
                if (_positionStorage.canLiquidate(positionKey)) {
                    batchPositionKeys[count] = positionKey;
                    count++;
                }
                i++;
                if (i == monitorSize) {
                    break;
                }
                if (count == batchSize) {
                    break;
                }
            }
        }

        upkeepNeeded = count > 0;
        if (upkeepNeeded) {
            performData = abi.encode(batchPositionKeys, count);
        }
    }

    function _close(
        bytes32 _positionKey,
        bytes memory _data0,
        bytes memory _data1,
        address _closer
    ) internal {
        IPositionStorage.TradePosition memory pos = IPositionStorage(
            positionStorage
        ).position(_positionKey);
        IPool(pos.pool).close(
            IPositionStorage.CloseTradePositionParams({
                positionKey: pos.positionKey,
                data0: _data0,
                data1: _data1,
                closer: _closer
            })
        );
    }

    // called by offchain monitor which uses meta aggregator
    function close(
        bytes32 _positionKey,
        bytes memory _data0,
        bytes memory _data1
    ) external {
        _close(_positionKey, _data0, _data1, msg.sender);
    }

    function performUpkeep(bytes calldata _performData) external override {
        uint256 usedGas = gasleft();
        (bytes32[] memory batchPositionKeys, uint256 count) = abi.decode(
            _performData,
            (bytes32[], uint256)
        );

        for (uint256 i = 0; i < count; i++) {
            _close(
                batchPositionKeys[i],
                new bytes(0),
                new bytes(0),
                address(this)
            );
        }

        emit ProcessBatch(
            count,
            _performData,
            usedGas - gasleft(),
            tx.gasprice
        );
    }

    // Gelato compatible function
    function checker()
        external
        view
        returns (bool canExec, bytes memory execPayload)
    {
        IPositionStorage _positionStorage = IPositionStorage(positionStorage);
        bytes32[] memory batchPositionKeys = new bytes32[](batchSize);
        uint256 count;
        uint256 i;

        uint256 positionLength = _positionStorage.positionLength();
        if (startIndex < positionLength) {
            for (uint256 j = startIndex; j < positionLength; j++) {
                bytes32 positionKey = _positionStorage.positionKeys(j);
                if (_positionStorage.canLiquidate(positionKey)) {
                    batchPositionKeys[count] = positionKey;
                    count++;
                }
                i++;
                if (i == monitorSize) {
                    break;
                }
                if (count == batchSize) {
                    break;
                }
            }
        }

        canExec = count > 0;
        if (canExec) {
            execPayload = abi.encodeWithSignature(
                "performUpkeep(bytes)",
                abi.encode(batchPositionKeys, count)
            );
        }
    }
}

contracts/Router.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.8.4;

import "./libraries/TransferHelper.sol";
import "./libraries/PoolAddress.sol";
import "./interfaces/external/IWETH9.sol";
import "./interfaces/ICloseCallback.sol";
import "./interfaces/IDEXAggregator.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IFactory.sol";
import "./interfaces/IPool.sol";
import "./interfaces/IMetaAggregator.sol";
import "./interfaces/IRouter.sol";
import "./base/PeripheryValidation.sol";

contract Router is IRouter, ICloseCallback, PeripheryValidation {
    address public immutable override WETH;
    address public immutable override factory;
    address public immutable poolDeployer;
    address public immutable positionStorage;
    address public manager;
    address public metaAggregator;

    error Forbidden(address sender);
    error ZeroAddress();
    error InvalidPool(address pool);
    error InsufficientInput();
    error InsufficientOutput();
    error InvalidParameters();

    event SetManager(address manager);
    event SetMetaAggregator(address manager);

    constructor(address _factory, address _WETH) {
        factory = _factory;
        WETH = _WETH;
        poolDeployer = IFactory(_factory).poolDeployer();
        positionStorage = IFactory(_factory).positionStorage();
    }

    modifier onlyManager() {
        if (msg.sender != manager) revert Forbidden(msg.sender);
        _;
    }

    receive() external payable {
        assert(msg.sender == WETH); // only accept ETH via fallback from the WETH contract
    }

    function setManager(address _newManager) external onlyManager {
        if (_newManager == address(0)) revert ZeroAddress();
        manager = _newManager;

        emit SetManager(_newManager);
    }

    function setMetaAggregator(
        address _newMetaAggregator
    ) external onlyManager {
        metaAggregator = _newMetaAggregator;

        emit SetMetaAggregator(_newMetaAggregator);
    }

    function preview(
        IRouter.OpenTradePositionParams memory _params
    ) external view override returns (IPositionStorage.TradePosition memory) {
        IPositionStorage _positionStorage = IPositionStorage(positionStorage);
        return
            _positionStorage.previewTradePosition(
                IPositionStorage.OpenTradePositionParams({
                    owner: msg.sender,
                    baseToken: _params.baseToken,
                    quoteToken: _params.quoteToken,
                    collateral: _params.collateral,
                    baseAmount: _params.baseAmount,
                    quoteAmount: _params.quoteAmount,
                    collateralAmount: _params.collateralAmount,
                    deadline: _params.deadline,
                    stoplossPrice: _params.stoplossPrice,
                    takeProfitPrice: _params.takeProfitPrice
                })
            );
    }

    function _openPosition(
        address _pool,
        IRouter.OpenTradePositionParams memory _params
    ) internal returns (bytes32 positionKey) {
        positionKey = IPool(_pool).open(
            IPositionStorage.OpenTradePositionParams({
                owner: msg.sender,
                baseToken: _params.baseToken,
                quoteToken: _params.quoteToken,
                collateral: _params.collateral,
                baseAmount: _params.baseAmount,
                quoteAmount: _params.quoteAmount,
                collateralAmount: _params.collateralAmount,
                deadline: _params.deadline,
                stoplossPrice: _params.stoplossPrice,
                takeProfitPrice: _params.takeProfitPrice
            })
        );
    }

    function open(
        IRouter.OpenTradePositionParams memory _params
    )
        external
        override
        checkDeadline(_params.txDeadline)
        returns (bytes32 positionKey)
    {
        address pool = PoolAddress.computeAddress(
            poolDeployer,
            _params.quoteToken
        );
        if (pool == address(0)) revert InvalidPool(pool);
        TransferHelper.safeTransferFrom(
            _params.collateral,
            msg.sender,
            pool,
            _params.collateralAmount
        );
        TransferHelper.safeTransferFrom(
            _params.baseToken,
            msg.sender,
            pool,
            _params.baseAmount
        );
        positionKey = _openPosition(pool, _params);
    }

    function openWithEthAsCollateral(
        IRouter.OpenTradePositionParams memory _params
    )
        external
        payable
        override
        checkDeadline(_params.txDeadline)
        returns (bytes32 positionKey)
    {
        if (_params.collateral != WETH) revert InvalidParameters();
        if (msg.value < _params.collateralAmount) revert InsufficientInput();
        address pool = PoolAddress.computeAddress(
            poolDeployer,
            _params.quoteToken
        );
        if (pool == address(0)) revert InvalidPool(pool);
        IWETH9(_params.collateral).deposit{value: _params.collateralAmount}();
        assert(
            IERC20(_params.collateral).transfer(pool, _params.collateralAmount)
        );
        TransferHelper.safeTransferFrom(
            _params.baseToken,
            msg.sender,
            pool,
            _params.baseAmount
        );
        positionKey = _openPosition(pool, _params);
    }

    function openWithEthAsBaseToken(
        IRouter.OpenTradePositionParams memory _params
    )
        external
        payable
        override
        checkDeadline(_params.txDeadline)
        returns (bytes32 positionKey)
    {
        if (_params.baseToken != WETH) revert InvalidParameters();
        if (msg.value < _params.baseAmount) revert InsufficientInput();
        address pool = PoolAddress.computeAddress(
            poolDeployer,
            _params.quoteToken
        );
        if (pool == address(0)) revert InvalidPool(pool);
        IWETH9(_params.baseToken).deposit{value: _params.baseAmount}();
        assert(IERC20(_params.baseToken).transfer(pool, _params.baseAmount));
        TransferHelper.safeTransferFrom(
            _params.collateral,
            msg.sender,
            pool,
            _params.collateralAmount
        );
        positionKey = _openPosition(pool, _params);
    }

    function openETH(
        IRouter.OpenTradePositionParams memory _params
    )
        external
        payable
        override
        checkDeadline(_params.txDeadline)
        returns (bytes32 positionKey)
    {
        address _WETH = WETH; // gas savings
        if (_params.baseToken != _WETH || _params.collateral != _WETH)
            revert InvalidParameters();
        uint256 totalInputAmount = _params.collateralAmount +
            _params.baseAmount;
        if (msg.value < totalInputAmount) revert InsufficientInput();
        address pool = PoolAddress.computeAddress(
            poolDeployer,
            _params.quoteToken
        );
        if (pool == address(0)) revert InvalidPool(pool);
        IWETH9(_params.collateral).deposit{value: totalInputAmount}();
        assert(IERC20(_params.collateral).transfer(pool, totalInputAmount));
        positionKey = _openPosition(pool, _params);
    }

    function closeCallback(
        address _tokenIn,
        address _tokenOut,
        uint256 _minAmountOut,
        bytes calldata _data
    ) external override {
        uint256 balance = IERC20(_tokenIn).balanceOf(address(this));
        uint256 amountOut;
        if (metaAggregator == address(0) || _data.length == 0) {
            IFactory _factory = IFactory(factory);
            IDEXAggregator aggregator = IDEXAggregator(
                _factory.dexAggregator()
            );

            TransferHelper.safeTransfer(_tokenIn, address(aggregator), balance);
            (amountOut, ) = aggregator.swap(
                address(0),
                _tokenIn,
                _tokenOut,
                _minAmountOut,
                address(this)
            );
        } else {
            IMetaAggregator aggregator = IMetaAggregator(metaAggregator);

            TransferHelper.safeTransfer(_tokenIn, address(aggregator), balance);
            amountOut = aggregator.swap(
                _tokenIn,
                _tokenOut,
                _minAmountOut,
                address(this),
                _data
            );
        }

        if (amountOut < _minAmountOut) revert InsufficientOutput();
        TransferHelper.safeTransfer(_tokenOut, address(msg.sender), amountOut);
    }

    function close(
        IRouter.CloseTradePositionParams memory _params
    ) external override checkDeadline(_params.txDeadline) {
        uint256 index = IFactory(factory).poolIndex(_params.pool);
        if (index == 0) revert InvalidPool(_params.pool);
        IPool(_params.pool).close(
            IPositionStorage.CloseTradePositionParams({
                positionKey: _params.positionKey,
                data0: _params.data0,
                data1: _params.data1,
                closer: msg.sender
            })
        );
    }

    function _handleServiceFee(
        IFactory _factory,
        address _serviceToken,
        uint256 _serviceFee
    ) internal {
        if (_serviceToken != address(0) && _serviceFee > 0) {
            address serviceFeeTo = _factory.serviceFeeTo();
            TransferHelper.safeTransfer(
                _serviceToken,
                serviceFeeTo,
                _serviceFee
            );
        }
    }

    function rollback(
        IRouter.RollbackTradePositionParams memory _params
    ) external override checkDeadline(_params.txDeadline) {
        IFactory _factory = IFactory(factory);
        uint256 index = _factory.poolIndex(_params.pool);
        if (index == 0) revert InvalidPool(_params.pool);

        IPositionStorage _positionStorage = IPositionStorage(positionStorage);
        IPositionStorage.TradePosition memory pos = _positionStorage.position(
            _params.positionKey
        );
        TransferHelper.safeTransferFrom(
            pos.quoteToken.id,
            msg.sender,
            _params.pool,
            pos.quoteToken.amount
        );

        address serviceToken = _factory.serviceToken();
        uint256 serviceFee = _factory.rollbackFee();
        _handleServiceFee(_factory, serviceToken, serviceFee);

        IPool(_params.pool).rollback(
            IPositionStorage.RollbackTradePositionParams({
                positionKey: _params.positionKey,
                rollbacker: msg.sender,
                serviceToken: serviceToken,
                serviceFee: serviceFee
            })
        );
    }

    function updateTPnSL(
        IRouter.UpdateTPnSLParams memory _params
    ) external override checkDeadline(_params.txDeadline) {
        IFactory _factory = IFactory(factory);
        uint256 index = _factory.poolIndex(_params.pool);
        if (index == 0) revert InvalidPool(_params.pool);

        address serviceToken = _factory.serviceToken();
        uint256 serviceFee = _factory.updateTPnSLFee();
        _handleServiceFee(_factory, serviceToken, serviceFee);

        IPositionStorage _positionStorage = IPositionStorage(positionStorage);
        _positionStorage.updateTPnSL(
            IPositionStorage.UpdateTPnSLParams({
                positionKey: _params.positionKey,
                takeProfitPrice: _params.takeProfitPrice,
                stoplossPrice: _params.stoplossPrice,
                updater: msg.sender,
                serviceToken: serviceToken,
                serviceFee: serviceFee
            })
        );
    }

    function updateCollateralAmount(
        IRouter.UpdateCollateralAmountParams memory _params
    )
        external
        override
        checkDeadline(_params.txDeadline)
        returns (uint256 collateralLiqPrice)
    {
        IFactory _factory = IFactory(factory);
        uint256 index = _factory.poolIndex(_params.pool);
        if (index == 0) revert InvalidPool(_params.pool);

        IPositionStorage _positionStorage = IPositionStorage(positionStorage);
        IPositionStorage.TradePosition memory pos = _positionStorage.position(
            _params.positionKey
        );
        TransferHelper.safeTransferFrom(
            pos.collateral.id,
            msg.sender,
            _params.pool,
            _params.amount
        );

        address serviceToken = _factory.serviceToken();
        uint256 serviceFee = _factory.updateCollateralAmountFee();
        _handleServiceFee(_factory, serviceToken, serviceFee);

        collateralLiqPrice = IPool(_params.pool).updateCollateralAmount(
            IPositionStorage.UpdateCollateralAmountParams({
                positionKey: _params.positionKey,
                amount: _params.amount,
                updater: msg.sender,
                serviceToken: serviceToken,
                serviceFee: serviceFee
            })
        );
    }

    function updateDeadline(
        IRouter.UpdateDeadlineParams memory _params
    ) external override checkDeadline(_params.txDeadline) {
        IFactory _factory = IFactory(factory);
        uint256 index = _factory.poolIndex(_params.pool);
        if (index == 0) revert InvalidPool(_params.pool);

        address serviceToken = _factory.serviceToken();
        uint256 serviceFee = _factory.updateDeadlineFee();
        _handleServiceFee(_factory, serviceToken, serviceFee);

        IPool(_params.pool).updateDeadline(
            IPositionStorage.UpdateDeadlineParams({
                positionKey: _params.positionKey,
                deadline: _params.deadline,
                updater: msg.sender,
                serviceToken: serviceToken,
                serviceFee: serviceFee
            })
        );
    }
}

