// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.8.4;
pragma abicoder v2;

import "../libraries/PoolAddress.sol";
import "../libraries/CallbackValidation.sol";
import "../interfaces/IMintCallback.sol";
import "./PeripheryPayments.sol";
import "./PeripheryImmutableState.sol";

/// @title Liquidity management functions
/// @notice Internal functions for safely managing liquidity
abstract contract LiquidityManagement is
    IMintCallback,
    PeripheryImmutableState,
    PeripheryPayments
{
    struct MintCallbackData {
        address token;
        address payer;
    }

    /// @inheritdoc IMintCallback
    function mintCallback(
        address _token,
        uint256 _liquidity,
        bytes calldata _data
    ) external override {
        MintCallbackData memory decoded = abi.decode(_data, (MintCallbackData));
        CallbackValidation.verifyCallback(poolDeployer, decoded.token);

        if (_liquidity > 0) pay(_token, decoded.payer, msg.sender, _liquidity);
    }

    /// @notice Add liquidity to an initialized pool
    function addLiquidity(
        address _token,
        uint256 _liquidity,
        address _to
    ) internal returns (IPool) {
        IPool pool = IPool(PoolAddress.computeAddress(poolDeployer, _token));

        pool.mint(
            _to,
            _liquidity,
            abi.encode(MintCallbackData({token: _token, payer: msg.sender}))
        );

        return pool;
    }
}

contracts/base/Multicall.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.8.4;
pragma abicoder v2;

contract Multicall {
    struct Call {
        address target;
        bytes callData;
    }
    struct Result {
        bool success;
        bytes returnData;
    }

    error CallFailed1();
    error CallFailed2();

    function aggregate(
        Call[] memory _calls
    ) public returns (uint256 blockNumber, bytes[] memory returnData) {
        blockNumber = block.number;
        returnData = new bytes[](_calls.length);
        for (uint256 i = 0; i < _calls.length; i++) {
            (bool success, bytes memory ret) = _calls[i].target.call(
                _calls[i].callData
            );
            if (!success) revert CallFailed1();
            returnData[i] = ret;
        }
    }

    function blockAndAggregate(
        Call[] memory _calls
    )
        public
        returns (
            uint256 blockNumber,
            bytes32 blockHash,
            Result[] memory returnData
        )
    {
        (blockNumber, blockHash, returnData) = tryBlockAndAggregate(
            true,
            _calls
        );
    }

    function getBlockHash(
        uint256 _blockNumber
    ) public view returns (bytes32 blockHash) {
        blockHash = blockhash(_blockNumber);
    }

    function getBlockNumber() public view returns (uint256 blockNumber) {
        blockNumber = block.number;
    }

    function getCurrentBlockCoinbase() public view returns (address coinbase) {
        coinbase = block.coinbase;
    }

    function getCurrentBlockDifficulty()
        public
        view
        returns (uint256 difficulty)
    {
        difficulty = block.difficulty;
    }

    function getCurrentBlockGasLimit() public view returns (uint256 gaslimit) {
        gaslimit = block.gaslimit;
    }

    function getCurrentBlockTimestamp()
        public
        view
        returns (uint256 timestamp)
    {
        timestamp = block.timestamp;
    }

    function getEthBalance(
        address _addr
    ) public view returns (uint256 balance) {
        balance = _addr.balance;
    }

    function getLastBlockHash() public view returns (bytes32 blockHash) {
        blockHash = blockhash(block.number - 1);
    }

    function tryAggregate(
        bool _requireSuccess,
        Call[] memory _calls
    ) public returns (Result[] memory returnData) {
        returnData = new Result[](_calls.length);
        for (uint256 i = 0; i < _calls.length; i++) {
            (bool success, bytes memory ret) = _calls[i].target.call(
                _calls[i].callData
            );

            if (_requireSuccess && !success) {
                revert CallFailed2();
            }

            returnData[i] = Result(success, ret);
        }
    }

    function tryBlockAndAggregate(
        bool _requireSuccess,
        Call[] memory _calls
    )
        public
        returns (
            uint256 blockNumber,
            bytes32 blockHash,
            Result[] memory returnData
        )
    {
        blockNumber = block.number;
        blockHash = blockhash(block.number);
        returnData = tryAggregate(_requireSuccess, _calls);
    }
}

